tr>\r\n<td>Tiêu chuẩn khí thải</td>\r\n<td>\r\n<p align=\"center\">Euro. III</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Hộp số</td>\r\n<td>\r\n<p align=\"center\">4 số tự động</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Kích thước &amp; trọng lượng</td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td>Dài x rộng x cao (mm)</td>\r\n<td>\r\n<p align=\"center\">4337x1839x1477</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Chiều dài cơ sở (mm)</td>\r\n<td>\r\n<p align=\"center\">2640</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Chiều rộng cơ sở trước sau (mm)</td>\r\n<td>\r\n<p align=\"center\">1535/1531</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Bán kính vòng quay tối thiểu (mm)</td>\r\n<td>\r\n<p align=\"center\">5575</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Khoảng sáng gầm xe tối thiểu (mm)</td>\r\n<td>\r\n<p align=\"center\">140-180</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Trọng lượng không tải (kg)</td>\r\n<td>\r\n<p align=\"center\">1321</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Trọng lượng toàn tải (kg)</td>\r\n<td>\r\n<p align=\"center\">1825</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Hệ thống phanh (Thắng)</td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td>Trước</td>\r\n<td>\r\n<p align=\"center\">Đĩa tản nhiệt</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Sau</td>\r\n<td>\r\n<p align=\"center\">Đĩa</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Hệ thống phanh ABS/EBD</td>\r\n<td>\r\n<p align=\"center\">Có ABS</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Hệ thống cân bằng điện tử ESP</td>\r\n<td>\r\n<p align=\"center\">Không</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Lốp</td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td>Lốp xe</td>\r\n<td>\r\n<p align=\"center\">205/55R16</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Vành (mâm) đúc hợp kim</td>\r\n<td>\r\n<p align=\"center\">Có</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Thiết bị an toàn</td>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td>Hệ thống túi khí an toàn ghế lái</td>\r\n<td>\r\n<p align=\"center\">Có</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Hệ thống túi khí an toàn ghế bên phía trước</td>\r\n<td>\r\n<p align=\"center\">có</p>\r\n</td>\r\n</tr>\r\n