insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (341,1,'2013-09-24 09:09:48','2013-09-24 09:09:48','<p style=\"text-align: justify;\">Ford Focus 2.0L AT (Ghia) – Kiểu dáng sang trọng và lịch lãm với</p>\r\n<p style=\"text-align: justify;\">- Mâm đúc hợp kim nhôm 205/55R16 – Thiết kế mới</p>\r\n<p style=\"text-align: justify;\">- Nẹp cánh cửa &amp; tay nắm cửa, hốc hút gió trước tích hợp đường viền Chrôm\r\n- Gương chiếu hậu tích hợp có đèn báo rẽ mang tính sang trọng\r\n- Trang bị hệ thống đèn sương mùa trước và sau tăng tính an toàn cho xe\r\n- Vô lăng 3 chấu bọc da , điều chỉnh 4 hướng\r\n-Gương chiếu hậu bên trong xe có chức năng tự động chống chói.\r\n- Có chức năng chọn bài và điều chỉnh âm thanh trên trục lái</p>\r\n<p style=\"text-align: justify;\">Có chức năng tự động bật đèn chiếu sáng phía trước</p>\r\n<p style=\"text-align: justify;\">- Hệ thống gạt nước thông minh , tự động gạt khi trời mưa\r\n- Hệ thống điều hòa tự động hai vùng khí hậu\r\n- Vô lăng điều chỉnh theo 4 hướng\r\n- CD 6 đĩa/ 6 Loa / MP3\r\n- Cổng kết nối AUX</p>\r\n<p style=\"text-align: center;\"><a href=\"http://huongauto.com/wp-content/uploads/2013/09/ford-focus-2-0.jpg\"><img class=\"wp-image-342 aligncenter\" alt=\"ford-focus-2-0\" src=\"http://huongauto.com/wp-content/uploads/2013/09/ford-focus-2-0.jpg\" width=\"800\" height=\"335\" /></a></p>\r\n<p style=\"text-align: justify;\">Thông số kỹ thuật Ford Fiesta Sedan</p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td><strong>Động cơ</strong></td>\r\n<td style=\"text-align: center;\"><strong>Xăng 2.0L Duratec-16Van</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Dung tích động cơ (cc)</td>\r\n<td>\r\n<p al